import React, { Component } from 'react'
import axios from 'axios'

export default class Counter extends Component {



  constructor (props) {
    super(props)
    this.state = {
      count: 1,
      load: false,
      disable: true,
      isEditing: false
    }
  }

  componentDidMount () {
    const url =
      'https://interview-8e4c5-default-rtdb.firebaseio.com/front-end/counter1.json'
    axios
      .get(url)
      .then(result => {
        this.setState({ count: result.data ? result.data : 0 })

        console.log(result.data)
      })
      .catch(error => {
        console.log(error)
      })
  }

  handleChange (e) {
    this.setState({ count: e.target.value })
  }

  handleKeyDown = event => {
    if (event.key === 'Enter') {
      // const url =
      //   'https://interview-8e4c5-default-rtdb.firebaseio.com/front-end.json'
      // var post = this.state.count

      // const params = JSON.stringify({
      //   parthkhanna: post
      // })
      // this.setState({ load: true })
      // axios
      //   .put(url, params, {
      //     headers: {
      //       'content-type': 'application/json; charset=utf-8'
      //     }
      //   })
      //   .then(result => {
      //     console.log(result)
      //     console.log(this.state.count)
      //   })
      //   .catch(error => {
      //     console.log(error)
      //   })
      // this.setState({ load: false })
      this.setState({ count:this.state.count})
      this.setState({ isEditing: false })
    }
  }

  handleIncrement = e => {
    // const url =
    //   'https://interview-8e4c5-default-rtdb.firebaseio.com/front-end.json'
    // var post = this.state.count ? this.state.count : 1

    // const params = JSON.stringify({
    //   parthkhanna: post
    // })
    // this.setState({ load: true })
    // axios
    //   .put(url, params, {
    //     headers: {
    //       'content-type': 'application/json; charset=utf-8'
    //     }
    //   })
    //   .then(result => {
    //     console.log(result)
    //     console.log(this.state.count)
    //   })
    //   .catch(error => {
    //     console.log(error)
    //   })
    // this.setState({ load: false })
    if(this.state.count!=1000)
      this.setState({count:this.state.count+1})
  }

  handleDecrement = e => {
    // const url =
    //   'https://interview-8e4c5-default-rtdb.firebaseio.com/front-end.json'
    // const post = this.state.count - 1

    // const params = JSON.stringify({
    //   prashant: post
    // })

    // this.setState({ load: true })

    // // console.log(user); console.log(post);
    // axios
    //   .put(url, params, {
    //     headers: {
    //       'content-type': 'application/json'
    //     }
    //   })
    //   .then(result => {})
    //   .catch(error => {
    //     console.log(error)
    //   })

    // this.setState({ load: false })

    if(this.state.count!=0)
      this.setState({count:this.state.count-1})
  }

  render () {
    return (
      <div
        className='container'
        style={{
          position: 'absolute',
          left: '50%',
          top: '50%',
          transform: 'translate(-50%, -50%)'
        }}
      >
        <div
          style={{ padding: '10px', height: '120%', width: '90%', backgroundColor: 'white' }}
        >
          {this.state.load ? <span>Saving counter value</span> : <span></span>}
          <div
            className='row'
            style={{
              height: '100%',

               display: 'flex'
            }}
          >



            <button
            id="button3"
              style={{ fontSize: '20px', fontWeight: 'bold', width: '70px', borderRadius: '20%' }}
              onClick={this.handleDecrement}
            >
              -
            </button>
            {this.state.isEditing ? (
              <input
                style={{ textAlign: 'center', width: '70px', color: 'red' }}
                type='text'
                defaultValue={this.state.count}
                onChange={this.handleChange.bind(this)}
                onKeyDown={this.handleKeyDown}
              />
            ) : (
              <button
              id="button2"
                style={{
                  borderRadius: '20%',
                  textAlign: 'center',
                  width: '70px',
                }}
                onClick={() => this.setState({ isEditing: true })}
              >
                <span style={{ fontSize: '20px', fontWeight: 'bold' }}>{this.state.count}</span>
              </button>
            )}
           <button
              id="button1"
              style={{ fontSize: '20px', fontWeight: 'bold', width: '70px', borderRadius: '20%' }}
              onClick={this.handleIncrement}
            >
              +
            </button>



          </div>
          <div  style={{textAlign: 'left',marginTop:"5px"}}>Counter Value: {this.state.count}</div>
        </div>
      </div>
    )
  }
}
