import React, { Component } from 'react'
import axios from 'axios'

export default class Counter extends Component {
  constructor (props) {
    super(props)
    this.state = {
      count: 1,
      load: false,
      disable:true
    }
  }

  componentDidMount () {
    const url =
      'https://interview-8e4c5-default-rtdb.firebaseio.com/front-end/counter1.json'
    axios
      .get(url)
      .then(result => {
        this.setState({ count: result.data ? result.data : 0 })

        console.log(result.data)
      })
      .catch(error => {
        console.log(error)
      })
  }

  handleClick(e){

    this.setState({disable:!this.state.disable})
    this.setState({count: e.target.value});
     }

  handleIncrement = e => {
    const url =
      'https://interview-8e4c5-default-rtdb.firebaseio.com/front-end.json'
    var post = this.state.count ? this.state.count : 1

    const params = JSON.stringify({
      parthkhanna: post
    })
    this.setState({load:true});
    axios
      .put(url, params, {
        headers: {
          'content-type': 'application/json; charset=utf-8'
        }
      })
      .then(result => {
        console.log(result)
        console.log(this.state.count)
      })
      .catch(error => {
        console.log(error)
      })
      this.setState({load:false});
  }

  handleDecrement = e => {
    const url =
      'https://interview-8e4c5-default-rtdb.firebaseio.com/front-end.json'
    const post = this.state.count - 1

    const params = JSON.stringify({
      prashant: post
    })

    this.setState({load:true});

    // console.log(user); console.log(post);
    axios
      .put(url, params, {
        headers: {
          'content-type': 'application/json'
        }
      })
      .then(result => {})
      .catch(error => {
        console.log(error)
      })

      this.setState({load:false});
  }

  render () {
    return (
      <div
        className='container'
        style={{

          position: 'absolute',
          left: '50%',
          top: '50%',
          transform: 'translate(-50%, -50%)'
        }}
      >
        <div
          style={{ padding:'20px', height: '120%', backgroundColor: 'yellow'}}
        >
          {this.state.load ? <span>Saving counter value</span> : <span></span>}
          <div
            className='row'
            style={{

              height: '100%',
              width: '100%',

              borderColor: 'red',
              borderWidth: '10px'
            }}
          >
            <button style={{ width: '55px',borderRadius: '20%'}} onClick={this.handleIncrement}>
              +
            </button>

            <input type="text" disabled={this.state.disable} value={this.state.count}  onClick={this.handleClick.bind(this)}/>

            {/* <button style={{ width: '55px', borderColor:"red"}}>{this.state.count}</button> */}
            <button style={{ width: '55px' , borderRadius: '20%'}} onChange={this.handleClick}>
             -
            </button>

            <br></br>
            Counter Value: {this.state.count} 

          </div>


        </div>
      </div>
    )
  }
}
