import React, { Component } from 'react'
import axios from 'axios'

export default class Counter extends Component {
  constructor (props) {
    super(props)
    this.state = {
      count: 1,
      load: false
    }
  }

  componentDidMount () {
    const url =
      'https://interview-8e4c5-default-rtdb.firebaseio.com/front-end/counter1.json'
    axios
      .get(url)
      .then(result => {
        this.setState({ count: result.data ? result.data : 0 })

        console.log(result.data)
      })
      .catch(error => {
        console.log(error)
      })
  }

  handleIncrement = e => {
    const url =
      'https://interview-8e4c5-default-rtdb.firebaseio.com/front-end.json'
    var post = this.state.count ? this.state.count : 1

    const params = JSON.stringify({
      parthkhanna: post
    })
    this.setState({load:true});
    axios
      .put(url, params, {
        headers: {
          'content-type': 'application/json; charset=utf-8'
        }
      })
      .then(result => {
        console.log(result)
        console.log(this.state.count)
      })
      .catch(error => {
        console.log(error)
      })
      this.setState({load:false});
  }

  handleDecrement = e => {
    const url =
      'https://interview-8e4c5-default-rtdb.firebaseio.com/front-end.json'
    const post = this.state.count - 1

    const params = JSON.stringify({
      prashant: post
    })

    this.setState({load:true});

    // console.log(user); console.log(post);
    axios
      .put(url, params, {
        headers: {
          'content-type': 'application/json'
        }
      })
      .then(result => {})
      .catch(error => {
        console.log(error)
      })

      this.setState({load:false});
  }

  render () {
    return (
      <div
        className='container'
        style={{
          position: 'absolute',
          left: '50%',
          top: '50%',
          transform: 'translate(-50%, -50%)'
        }}
      >
        <div
          style={{ height: '50px', width: '150px', backgroundColor: 'yellow' }}
        >
          {this.state.load ? <span>Saving counter value</span> : <span></span>}
          <div
            className='row'
            style={{
              padding: 'auto',
              height: '100%',
              width: '100%',
              backgroundColor: 'yellow',
              borderColor: 'red',
              borderWidth: '10px'
            }}
          >
            <button style={{ width: '55px' }} onClick={this.handleIncrement}>
              +
            </button>
            <span style={{ width: '50px' }}>{this.state.count}</span>
            <button style={{ width: '55px' }} onClick={this.handleDecrement}>
              -
            </button>
          </div>
        </div>
      </div>
    )
  }
}
