import React, { Component } from "react";
import axios from 'axios'


export default class Counter extends Component {

  constructor(props) {
    super(props);
    this.state = {
      count: 1,
      load:false
    }

  }

  componentDidMount () {
    // if (!localStorage.getItem('isLoggedIn')) {
    //   {
    //     this.props.history.push('/')
    //   }
    // }
    // const headers = {
    //   'Content-Type': 'application/json',
    //   Accept: 'application/json',
    //   Authorization: `JWT ${localStorage.getItem('token')}`
    // }
    const url = "https://interview-8e4c5-default-rtdb.firebaseio.com/front-end/counter1.json"
    axios
      .get(url)
      .then(result => {
        this.setState({ count: result })

         console.log(result.data);
      })
      .catch(error => {
        console.log(error)
      })
  }

  handleIncrement = (e) => {
    // const headers = {
    //   'Content-Type': 'application/json',
    //   Accept: 'application/json',
    //   Authorization: `JWT ${localStorage.getItem('token')}`
    // }

    const url="https://interview-8e4c5-default-rtdb.firebaseio.com/front-end.json";
    const post = this.state.count+1;
    let bodyFormData = new FormData()
    bodyFormData.set('counter1', post)


    // console.log(user); console.log(post);
    axios
      .put(url, bodyFormData)
      .then(result => {
        // this.setState({ posts: result.data });
        // console.log(result.data);
      })
      .catch(error => {
        console.log(error)
      })

    // window.setTimeout(function () {
    //   window.location.reload()
    // }, 200)
  }

  handleDecrement = (e) => {
    // const headers = {
    //   'Content-Type': 'application/json',
    //   Accept: 'application/json',
    //   Authorization: `JWT ${localStorage.getItem('token')}`
    // }

    const url="https://interview-8e4c5-default-rtdb.firebaseio.com/front-end.json";
    const post = this.state.count-1;
    let bodyFormData = new FormData()
    bodyFormData.set('counter1', post)


    // console.log(user); console.log(post);
    axios
      .put(url, bodyFormData)
      .then(result => {
        // this.setState({ posts: result.data });
        // console.log(result.data);
      })
      .catch(error => {
        console.log(error)
      })

    // window.setTimeout(function () {
    //   window.location.reload()
    // }, 200)
  }


  render() {


    return (
      <div className="container" style={{
        position: 'absolute', left: '50%', top: '50%',
        transform: 'translate(-50%, -50%)'
    }}>

    <div style={{height: '50px', width: '150px',backgroundColor: 'yellow'}}>
     {this.state.load?(<span>Saving counter value</span>):(<span></span>)}
      <div className="row" style={{padding:"auto", height: '100%', width: '100%',backgroundColor: 'yellow',borderColor: 'red',borderWidth:"10px"}}>
        <button style={{width:"55px"}} onClick={this.handleIncrement} >+</button>
        <span style={{width:"50px"}}>{this.state.count}</span>
        <button style={{width:"55px"}} onClick={this.handleDecrement}>-</button>
      </div>
      </div>

      </div>
    );
  }
}
